mapper.select:
SELECT * FROM
applyment 
WHERE artworkId = #{artworkId}
AND memberId = #{memberId}
AND delStatus = 0

SELECT id FROM
applyment 
WHERE relId IN

	#{relId}

AND relTypeCode = #{relTypeCode} 
AND delStatus = 0

SELECT id FROM
applyment 
WHERE relId = #{relId}
AND relTypeCode = #{relTypeCode} 
AND delStatus = 0

SELECT *
FROM applyment AS AM
WHERE AM.relId = #{relId}
AND
AM.relTypeCode = #{relTypeCode}
AND AM.memberId = #{memberId}
AND
AM.delStatus = 0

mapper.update:
UPDATE applyment SET updateDate = NOW(), delStatus = 1, delDate = NOW() WHERE relId IN
#{relId}
AND relTypeCode = #{relTypeCode}

UPDATE applyment
SET updateDate = NOW(),
delStatus = 1,
delDate = NOW()
WHERE relId = #{relId}
AND relTypeCode = #{relTypeCode}

UPDATE applyment

	updateDate = NOW(),

WHERE id = #{id}

UPDATE applyment

	updateDate = NOW(),
	result = #{result}

WHERE id = #{id}

UPDATE applyment

	updateDate = NOW(),
	alarmDate = NOW(),
	alarmStatus = #{alarmStatus}

WHERE id = #{id}

UPDATE applyment

	updateDate = NOW(),
	checkDate = NOW(),
	checkStatus = #{checkStatus}

WHERE id = #{id}
