mapper.select:
SELECT *
FROM
actingRole
WHERE
artworkId = #{artworkId}

	LIMIT #{limitStart} , #{limitTake}

SELECT * FROM actingRole
WHERE artworkId = #{artworkId}
AND
gender = #{gender}
AND age = #{age}
AND role = #{role}

SELECT * FROM actingRole
WHERE artworkId = #{artworkId}
AND role IN

	#{role}

SELECT ACR.*, ACRF.id AS actingRole__id, ACRF.updateDate AS actingRole__updateDate, ACRF.relTypeCode AS actingRole__relTypeCode, ACRF.fileExt AS actingRole__fileExt, ACRF.typeCode AS actingRole__typeCode, ACRF.fileDir AS actingRole__fileDir, MF.id AS member__id, MF.updateDate AS member__updateDate, MF.relTypeCode AS member__relTypeCode, MF.fileExt AS member__fileExt, MF.fileDir AS member__fileDir, ARW.title AS artwork__title, ARW.genre AS artwork__genre, ARW.directorName AS artwork__directorName, M.id AS writer__id, M.authority AS writer__authority FROM actingRole AS ACR LEFT OUTER JOIN artwork AS ARW ON ACR.artworkId = ARW.id LEFT OUTER JOIN member AS M ON ARW.memberId = M.id LEFT OUTER JOIN file AS ACRF ON ACR.id = ACRF.relId AND ACRF.relTypeCode = "actingRole"
AND ACRF.typeCode = #{typeCode}
LEFT OUTER JOIN file MF ON ARW.memberId = MF.relId AND MF.relTypeCode = "member"

mapper.insert:
INSERT INTO actingRole SET regDate = NOW(), updateDate = NOW(), startDate = #{startDate}, endDate = #{endDate},
artworkId = #{artworkId},
role = #{role}, pay = #{pay}, age = #{age}, gender = #{gender}, job = #{job}, feature = #{feature}, region = #{region}, schedule = #{schedule}, shotAngle = #{shotAngle}, scriptStatus = #{scriptStatus}, guideVideoUrl = #{guideVideoUrl}, shootingsCount = #{shootingsCount}

mapper.update:
UPDATE `actingRole`
SET updateDate = NOW(),
artworkId =
#{artworkId}
WHERE id = #{id}
