mapper.select:
SELECT *
FROM file
WHERE 1
AND relId IN

	#{relId}

AND relTypeCode = #{relTypeCode}
AND typeCode = #{typeCode}
AND
type2Code = #{type2Code}
AND fileNo = #{fileNo}
AND delStatus = 0
ORDER BY relId

SELECT *
FROM file
WHERE 1
AND relId IN

	#{relId}

AND relTypeCode = #{relTypeCode}
AND typeCode = #{typeCode}
AND
type2Code = #{type2Code}
AND delStatus = 0
ORDER BY relId, fileNo

SELECT *
FROM file
WHERE 1
AND relId = #{relId}
AND relTypeCode
= #{relTypeCode}
AND typeCode = #{typeCode}
AND
type2Code = #{type2Code}
AND delStatus = 0
ORDER BY fileNo

SELECT *
FROM file
WHERE 1
AND relId = #{relId}
AND relTypeCode
= #{relTypeCode}
AND typeCode = #{typeCode}
AND
type2Code = #{type2Code}
AND delStatus = 0
ORDER BY fileNo

mapper.update:
UPDATE `file`
SET	updateDate = NOW(),
relId = #{relId}
WHERE id = #{id}
AND delStatus = 0

UPDATE file
SET delDate = NOW(),
delStatus = 1
WHERE relId = #{relId}
AND relTypeCode
= #{relTypeCode}

mapper.select:
SELECT id
FROM file
WHERE
relTypeCode = #{relTypeCode}
AND relId = #{relId}
AND typeCode =
#{typeCode}
AND type2Code = #{type2Code}
AND fileNo = #{fileNo}
