mapper.select:
SELECT *
FROM member
WHERE
name = #{name}
AND email = #{email}

SELECT *
FROM member
WHERE loginId LIKE CONCAT('%',#{loginId},'%')
AND authority = #{authority}
AND id != #{id}

mapper.update:
UPDATE member
updateDate = NOW(), `name` = #{name}, `nickname` = #{nickname}, `email` = #{email}, `cellphoneNo` = #{cellphoneNo}, `youtubeUrl` = #{youtubeUrl}, `loginPw` = #{loginPw},
WHERE id = #{id}
