mapper.select:
SELECT *
FROM
member
WHERE
name = #{name}
AND email = #{email}

mapper.update:
UPDATE member
updateDate = NOW(), `youTubeUrl` = #{youTubeUrl}, `name` = #{name}, `careerId` = #{careerId}, `nickname` = #{nickname}, `email` = #{email}, `cellphoneNo` = #{cellphoneNo}, `loginPw` = #{loginPw}, `careerId` = #{careerId}, `ISNI_number` = #{ISNI_number},
WHERE id = #{id}
